import React, { useState, useRef, useEffect } from 'react'
import './questionbox.css'
import TextareaAutosize from 'react-autosize-textarea';



export const Questionbox = (props) => {

    const [isEdit, setIsEdit] = useState(false);
    const [currentAnswer, setCurrentAnser] = useState();
    const textAreaRef = useRef(null);

    useEffect (() => {
        setCurrentAnser(props.answer);
    }, []);

    useEffect(() => {
        autosize(textAreaRef.current);
    }, [currentAnswer]);

    const currentAnswerHandler = (e) => {
        e.preventDefault();
        setCurrentAnser(e.target.value);
    }

    function changeIsEdit() {
        setIsEdit(!isEdit);
    }

    function changeAnswer() {
        props.updateAnswer(currentAnswer);
        setIsEdit(false);
    }

    return(
        <>
            <div className='questionbox'>
                <div className='contatiner'>
                    <div className='questionbar'>
                        <div className='question'>
                            {props.question}
                        </div>
                        <div className='buttons'>
                            <div className='makepublic'>
                                make public
                            </div>
                            <div>
                                toggle
                            </div>
                            <button onClick={props.deleteQuestion}>delete</button>
                        </div>
                    </div>
                    <div className='answerbox'>
                        <div className='answer'>
                            {isEdit ? <TextareaAutosize className='editAnswer' ref={textAreaRef} value={currentAnswer} onChange={currentAnswerHandler}/> : <div>{currentAnswer}</div>}
                        </div>
                        <div className='buttonContainer'>
                            {isEdit ? <div><button onClick={changeIsEdit}>cancel</button> <button onClick={changeAnswer}>complete</button></div> : <button onClick={changeIsEdit}>Edit the Answer</button>}
                        </div>
                    </div>
                </div>
                <img className='handle' src="images/handle.png" alt="handle" />
            </div>
        </>
    )
}
