import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useLocation } from 'react-router-dom';

import { Topbar } from '../Components/Topbar/topbar';
import { Questionbox } from '../Components/Questionbox/questionbox';
import { Recommendquestion } from '../Components/Recommendquestion/recommendquestion';
import './page.css'


export const MainPage = (props) => {

    const location = useLocation();
    const { url } = location.state;
    const[title, setTitle] = useState("Title of Research Paper");
    const[authors, setAuthors] = useState("Author Name");
    const[currentQuestion, setCurrentQuestion] = useState("");
    const[QnAs, setQnAs] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const getTitleApi = 'https://qna-restapi-dxpyj.run.goorm.site/getQuestion/' + String(url).split('/').pop();
                const result = await axios(getTitleApi);
                setTitle(String(result.data));
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    const currentQuestionHandler = (e) => {
        e.preventDefault();
        setCurrentQuestion(e.target.value);
    }

    function addQuestion (question) {
        const answer = "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industr";
        setQnAs(prevData => [...prevData, {question: String(question), answer: String(answer), isPublic: false}]);
    }

    function deleteQuestion (index) {
        setQnAs(prevData => {
            let newArray = [...prevData];
            newArray.splice(index, 1);
            return newArray;
        });
    }

    function addCurrentQuestion () {
        if(currentQuestion != "") {
            addQuestion(currentQuestion);
            setCurrentQuestion("");
        }
    }

    return(
        <>
            <div className='mainPage'>
                <Topbar/>
                <div className='container'>
                    <div className='title'>
                        {title}
                    </div>
                    <div className='authors'>
                        {authors}
                    </div>
                    <div className='previewContainer'>
                        <button>
                            preview
                        </button>
                    </div>
                    <div className='subtitle'>
                        Add Question
                    </div>
                    <div className='inputContainer'>
                        <input value={currentQuestion} onChange={currentQuestionHandler} placeholder='Type Your Own Question'></input>
                        <button onClick={addCurrentQuestion}>Add +</button>
                    </div>
                    <div className='subtitle'>
                        Question Recommendation
                    </div>
                    <div className='recommendContainer'>
                        <button>Recommend Question</button>
                        <Recommendquestion/>
                        <Recommendquestion/>
                    </div>
                    <div className='subtitle'>
                        QnA
                    </div>
                    <div className='questionContainer'>
                        {QnAs.map((QnA, index) => (
                            <Questionbox key={index} question={QnA.question} answer={QnA.answer} isPublic={QnA.isPublic} deleteQuestion={() => deleteQuestion(index)}/>
                        ))}
                    </div>
                </div>
                <div className='footer'>
                    
                </div>
            </div>
        </>
    )
}
